// quickSortBy :: (a -> a -> Ordering) -> [a] -> [a]
const quickSortBy = (cmp, xs) =>
    xs.length > 1 ? (() => {
        const
            h = xs[0],
            lessMore = partition(
                x => cmp(x, h) !== 1,
                xs.slice(1)
            );
        return [].concat.apply(
            [], [quickSortBy(cmp, lessMore[0]), h, quickSortBy(cmp, lessMore[1])]
        );
    })() : xs;
