// bimap :: (a -> b) -> (c -> d) -> (a, c) -> (b, d)
const bimap = f =>
    // Tuple instance of bimap.
    // A tuple of the application of f and g to the
    // first and second values respectively.
    g => tpl => 2 !== tpl.length ? (
        bimapN(f)(g)(tpl)
    ) : Tuple(f(tpl[0]))(
        g(tpl[1])
    );
