// unfoldTree :: (b -> (a, [b])) -> b -> Tree a
const unfoldTree = f =>
    // A tree constructed in breadth-first order
    // from a seed value.
    // f is applied to the tree Node b [], and then
    // to each root value in the tree's leaves, to 
    // generate its sub-forest (nest).
    b => uncurry(Node)(
        second(unfoldForest(f))(
            f(b)
        )
    );
